#include <gtest/gtest.h>
#include "nuca.h"

typedef RemoveNs<CompressingBitHandler<DecompressingBitHandler<AddNs<StringTestLayer<EndLayer> > > > > TestCompressor;

TEST(CompressingDecompressingTest, compressingDecompressing1)
{
    std::string out;
    const std::string sequence = "";
    TestCompressor cmpr;
    cmpr.setOutputString(out);

    for (std::string::const_iterator it = sequence.begin(); it < sequence.end(); ++it)
        cmpr.receiveData(*it);

    cmpr.receiveData(nuca::EndSeq);

    ASSERT_EQ(out, sequence);
}

