#include <gtest/gtest.h>
#include "nuca.h"

TEST(Compressing, compressingNs1)
{
    std::string out;
    const std::string sequence = "";
    CompressingFSM fsm(out);

    for (std::string::const_iterator it = sequence.begin(); it < sequence.end(); ++it)
        fsm.stimulate(*it);

    fsm.stimulate(EndSeq);

    ASSERT_EQ(out, "ACTAAAAACTGGGGACTGGGGACTGGGGACG");
}

