TEST(Compressing, compressingNs3)
{
    std::string out;
    const std::string sequence = "ACNACTG";
    CompressingFSM fsm(out);

    for (std::string::const_iterator it = sequence.begin(); it < sequence.end(); ++it)
        fsm.stimulate(*it);

    fsm.stimulate(EndSeq);

    ASSERT_EQ(out, "ACACTAAATACTAAAAG");
}
