#include <gtest/gtest.h>
#include "nuca.h"

typedef RemoveNs<StringTestLayer<CompressingBitHandler<OstreamSaver<EndLayer> > > > TestCompressor;

TEST(Compressing, compressingNs1)
{
    std::ofstream of("file.out", std::ios_base::binary);
    std::string out;
    const std::string sequence = "";
    TestCompressor cmpr;
    cmpr.setOutputString(out);
    cmpr.setOstream(of);

    for (std::string::const_iterator it = sequence.begin(); it < sequence.end(); ++it)
        cmpr.receiveData(*it);

    cmpr.receiveData(nuca::EndSeq);
    of.close();

    ASSERT_EQ(out, "TACGACAAAATACGACGGGGTACGACGGGGTACGACGGGGACG");
}

